import React, { useState } from "react";

import { validateEmail } from "../../utils/helpers";

function Contact() {
  const [email, setEmail] = useState("");
  const [userName, setUserName] = useState("");
  const [message, setMessage] = useState("");

  const [errorMessage, setErrorMessage] = useState("");

  const handleInputChange = (e) => {
    const { target } = e;
    const inputType = target.name;
    const inputValue = target.value;

    if (inputType === "email") {
      setEmail(inputValue);
    } else if (inputType === "userName") {
      setUserName(inputValue);
    } else {
      setMessage(inputValue);
    }
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();

    if (!validateEmail(email) || !userName) {
      setErrorMessage("Email or username is invalid");
      return;
    }
    if (!message) {
      setErrorMessage(`Message must be`);
      return;
    }
    alert(`Hello ${userName}`);

    setUserName("");
    setMessage("");
    setEmail("");
  };

  return (
    <section class="mb-4">
      <div class="row">
        <div class="col-md-9 mb-md-0 mb-5">
          <form
            id="contact-form"
            name="contact-form"
            action="mail.php"
            method="POST"
          >
            <div class="row">
              <div class="col-md-6">
                <div class="md-form mb-0">
                  <input
                    value={userName}
                    name="userName"
                    onChange={handleInputChange}
                    type="text"
                    placeholder="username"
                  />
                </div>
              </div>

              <div class="col-md-6">
                <div class="md-form mb-0">
                  <input
                    value={email}
                    name="email"
                    onChange={handleInputChange}
                    type="email"
                    placeholder="email"
                  />
                </div>
              </div>
            </div>

            <div class="row">
              <div class="col-md-12">
                <div class="md-form">
                  <input
                    value={message}
                    name="message"
                    onChange={handleInputChange}
                    type="text"
                    placeholder="message"
                  />
                </div>
              </div>
            </div>
          </form>

          <div class="text-center text-md-left">
          <button type="button" onClick={handleFormSubmit}>
    //       Submit
    //     </button>
          </div>
          <div id="status"></div>
        </div>
      </div>
    </section>

    // <div>
    //   <p>Hello {userName}</p>
    //   <form className="form">
    //     <input
    //       value={email}
    //       name="email"
    //       onChange={handleInputChange}
    //       type="email"
    //       placeholder="email"
    //     />
    //     <input
    //       value={userName}
    //       name="userName"
    //       onChange={handleInputChange}
    //       type="text"
    //       placeholder="username"
    //     />
    //     <input
    //       value={message}
    //       name="message"
    //       onChange={handleInputChange}
    //       type="text"
    //       placeholder="message"
    //     />
    //     <button type="button" onClick={handleFormSubmit}>
    //       Submit
    //     </button>
    //   </form>
    //   {errorMessage && (
    //     <div>
    //       <p className="error-text">{errorMessage}</p>
    //     </div>
    //   )}
    // </div>
  );
}

export default Contact;
