    //       onChange={handleInputChange}
    //       type="text"
    //       placeholder="message"
    //     />
    //     <button type="button" onClick={handleFormSubmit}>
    //       Submit
    //     </button>
    //   </form>
    //   {errorMessage && (
    //     <div>
    //       <p className="error-text">{errorMessage}</p>
    //     </div>
    //   )}
    // </div>
  );
}

export default Contact;
